create view household_master_view as SELECT "household_master"."id","household_master"."fk_district_id","household_master"."fk_thana_id","household_master"."fk_slum_id","household_master"."fk_slum_area_id", "household_master"."household_code" as "HHNO", "household_master"."contact_number" as "MOB1", "household_master"."houseCount", "household_master"."barino" as "BARINO", "household_master"."bariwalla_name" as "BARIWALA", "household_master"."household_head_name" as "HEADNM", "household_master"."longlivy" as "LONGLIVY", "household_master"."longlivm" as "LONGLIVM", "household_master"."leftpad" as "LEFTPAD", FORMAT(household_master.entry_date, 'dd/MM/yyyy') as entry_date, "household_master"."round_master_id_entry_round", "household_master"."migration_reason_oth", FORMAT(household_master.extinct_date, 'dd/MM/yyyy') as extinct_date, "household_master"."round_master_id_extinct_round", "household_master"."location_id", "household_master"."location_split_id", "tbl_district"."code" as "district_code", "tbl_thana"."code" as "thana_code", "tbl_slum"."code" as "slum_code", "tbl_slum_area"."code" as "slum_area_code", "fk_entry_type"."code" as "fk_entry_type_code", "fk_migration_reason"."code" as "fk_migration_reason_code", "tbl_country"."code" as "country_from_code", "district_from"."code" as "district_from_code", "thana_from"."code" as "thana_from_code", "slum_from"."code" as "slum_from_code", "slum_area_from"."code" as "slum_area_from_code", "fk_extinct_type"."code" as "fk_extinct_type_code", "fk_contract_type"."code" as "fk_contract_type_code", "fk_family_type"."code" as "fk_family_type_code", "fk_study_design"."code" as "fk_study_design_code", "tbl_member_master"."member_code" as "member_code_last_head", FORMAT(household_master.insertedOn, 'dd/MM/yyyy') as insertedDate, FORMAT(household_master.insertedOn, 'HH:mm:ss') as insertedTime, "insertedBy"."name" as "insertedBy_name", FORMAT(household_master.updatedOn, 'dd/MM/yyyy') as updatedDate, FORMAT(household_master.updatedOn, 'HH:mm:ss') as updatedTime, "updateBy"."name" as "updateBy_name"
FROM "household_master"
LEFT JOIN "tbl_district" ON "household_master"."fk_district_id" = "tbl_district"."id"
LEFT JOIN "tbl_thana" ON "household_master"."fk_thana_id" = "tbl_thana"."id"
LEFT JOIN "tbl_slum" ON "household_master"."fk_slum_id" = "tbl_slum"."id"
LEFT JOIN "tbl_slum_area" ON "household_master"."fk_slum_area_id" = "tbl_slum_area"."id"
LEFT JOIN "tbl_lookup_details" as "fk_entry_type" ON "household_master"."fk_entry_type" = "fk_entry_type"."id"
LEFT JOIN "tbl_lookup_details" as "fk_migration_reason" ON "household_master"."fk_migration_reason" = "fk_migration_reason"."id"
LEFT JOIN "tbl_lookup_details" as "fk_extinct_type" ON "household_master"."fk_extinct_type" = "fk_extinct_type"."id"
LEFT JOIN "tbl_lookup_details" as "fk_contract_type" ON "household_master"."fk_contract_type" = "fk_contract_type"."id"
LEFT JOIN "tbl_lookup_details" as "fk_family_type" ON "household_master"."fk_family_type" = "fk_family_type"."id"
LEFT JOIN "tbl_lookup_details" as "fk_study_design" ON "household_master"."fk_study_design" = "fk_study_design"."id"
LEFT JOIN "tbl_country" ON "household_master"."fk_country_id_from" = "tbl_country"."id"
LEFT JOIN "tbl_district" as "district_from" ON "household_master"."fk_district_id_from" = "district_from"."id"
LEFT JOIN "tbl_thana" as "thana_from" ON "household_master"."fk_thana_id_from" = "thana_from"."id"
LEFT JOIN "tbl_slum" as "slum_from" ON "household_master"."fk_slum_id_from" = "slum_from"."id"
LEFT JOIN "tbl_slum_area" as "slum_area_from" ON "household_master"."fk_slumArea_id_from" = "slum_area_from"."id"
LEFT JOIN "tbl_member_master" ON "household_master"."member_master_id_last_head" = "tbl_member_master"."id"
LEFT JOIN "tbl_users" as "insertedBy" ON "insertedBy"."userId"="household_master"."insertedBy"
LEFT JOIN "tbl_users" as "updateBy" ON "updateBy"."userId"="household_master"."updateBy"